create table wksp_wsdtbdev.sp_account_documents (
    id            number generated by default on null as identity not null enable,
    account_id    number not null enable,
    file_name     varchar2(255 byte) not null enable,
    file_data     blob not null enable,
    created_on    date not null enable,
    created_by    varchar2(255 byte) not null enable,
    updated_on    date not null enable,
    updated_by    varchar2(255 byte) not null enable,
    row_version   number(*, 0) not null enable,
    mime_type     varchar2(255 byte),
    document_type varchar2(3 byte)
);

alter table wksp_wsdtbdev.sp_account_documents
    add constraint sp_account_documents_pk primary key ( id )
        using index enable;

