create table wksp_wsdtbdev.sp_account_comments (
    id               number generated by default on null as identity not null enable,
    account_id       number not null enable,
    comment_date     date not null enable,
    comment_text     varchar2(1024 char) not null enable,
    comment_username varchar2(50 byte) default 'SYSTEM'
);

alter table wksp_wsdtbdev.sp_account_comments
    add constraint sp_account_comments_pk primary key ( id )
        using index enable;


