create table sp_account_details (
    id             number generated by default on null as identity not null enable,
    account_id     number not null enable,
    company_name   varchar2(255 byte),
    contact_person varchar2(255 byte),
    address        varchar2(4000 byte),
    phone          varchar2(255 byte),
    email          varchar2(255 byte),
    taxpin         varchar2(255 byte),
    created_on     date not null enable,
    created_by     varchar2(255 byte) not null enable,
    updated_on     date not null enable,
    updated_by     varchar2(255 byte) not null enable,
    row_version    number(*, 0) not null enable
);

alter table sp_account_details
    add constraint sp_account_details_pk primary key ( id )
        using index enable;


