create table sp_accounts (
    id                    number generated by default on null as identity not null enable,
    email                 varchar2(255 byte) not null enable,
    phone                 varchar2(255 byte) not null enable,
    password              varchar2(255 byte) not null enable,
    created_on            date not null enable,
    created_by            varchar2(255 byte) not null enable,
    updated_on            date not null enable,
    updated_by            varchar2(255 byte) not null enable,
    row_version           number(*, 0) not null enable,
    expired               varchar2(1 byte) default 'Y' not null enable,
    sp_username           varchar2(255 byte) not null enable,
    account_was_submitted varchar2(1 byte) default 'N' not null enable,
    locked_account        varchar2(1 byte) default 'N',
    account_is_approved   varchar2(1 byte) default 'N'
);

alter table sp_accounts
    add constraint sp_accounts_pk primary key ( id )
        using index enable;

alter table sp_accounts add constraint sp_account_name_uk unique ( sp_username )
    using index enable;


